mapper.select:
select product_id as productId, sd_product_id as sdProductId, cat_lv1_id as catLv1Id, cat_lv2_id as catLv2Id, cat_lv3_id as catLv3Id, path_name as pathName, alter_href as alterHref from product
cat_lv1_id = #{cate1} and cat_lv2_id = #{cate2} and cat_lv3_id = #{cate3}
limit ${pagingParams.size} offset ${pagingParams.offset}
limit 20

select product_id as productId, sd_product_id as sdProductId, cat_lv1_id as catLv1Id, cat_lv2_id as catLv2Id, cat_lv3_id as catLv3Id, path_name as pathName, alter_href as alterHref from product order by RAND()
limit ${pagingParams.size} offset ${pagingParams.offset}
limit 20
